create dba procedure informix.ifx_load_module (modulename varchar(255), 
        languagename varchar(255)) returns int

     define retvalue int;
     let retvalue = reload_module(modulename, languagename);
     if retvalue = 0 then
         return 0;
     else
         raise exception -9720;
     end if;
end procedure;

