create dba procedure informix.ifx_trigger_action(trigid int, when CHAR(10))
	returns informix.lvarchar; 
	define cleanwhen	char(10);
	define intwhen		int;

	let cleanwhen = trim (BOTH FROM when);
	if (cleanwhen = 'BEFORE' or cleanwhen = 'before') then
	    let intwhen = 1;
	elif (cleanwhen = 'ROW' or cleanwhen = 'row') then
	    let intwhen = 2;
	elif (cleanwhen = 'AFTER' or cleanwhen = 'after') then
	    let intwhen = 3;
	else
	    return NULL;
	end if;

	return (informix.trigger_action(trigid, intwhen));
end procedure;

create dba procedure informix.ifx_trigger_cols(trigid int)
	returns informix.lvarchar
	return (informix.trigger_cols(trigid));
end procedure;

create procedure informix.ifx_unload_module (modulename varchar(255), 
        languagename varchar(255)) returns int

     define retvalue int;
     let retvalue = unloading_module(modulename, languagename);
     if retvalue = 0 then
         return 0;
     end if;
     if retvalue = -1 then
         raise exception -9721;
     else
         raise exception -9720;
     end if;
end procedure;

create procedure informix.is_pattern_arg(str1 varchar(255), options char(4000)) returning integer

return 1;
end procedure;

CREATE FUNCTION "informix".jm3_export(load_path VARCHAR(50) ) 
       RETURNING INTEGER, INTEGER
DEFINE rc                INTEGER;
DEFINE bad               INTEGER;
DEFINE load_dir 	 LVARCHAR(512);
DEFINE create_ext_tab    LVARCHAR(8192);
DEFINE ins               LVARCHAR(512);
DEFINE drop_ext_tab      LVARCHAR(512);
DEFINE tname             VARCHAR(250);
DEFINE ext_tname         VARCHAR(250);
DEFINE dbschema          LVARCHAR(1024);
DEFINE dbname            VARCHAR(250);
DEFINE mkdir_cmd         VARCHAR(250);
DEFINE numrows           INTEGER;

LET rc=0;
LET bad=0;

--SET DEBUG FILE TO "/tmp/jm3.log";
--TRACE ON;
LET dbname = DBINFO("dbname")  ;
LET load_dir = TRIM(load_path) || "/" || TRIM(dbname) || ".exp" ;

LET mkdir_cmd = "mkdir -p " || load_dir;
SYSTEM mkdir_cmd ;

SET ISOLATION TO DIRTY READ;

FOREACH SELECT   TRIM(tabname) ,nrows
     INTO tname,numrows
     FROM systables
     WHERE tabid >99 AND tabtype = "T"

     LET ext_tname = tname||"_ext" ;
     LET drop_ext_tab = "DROP TABLE IF EXISTS "||ext_tname;
     EXECUTE IMMEDIATE drop_ext_tab;

     LET create_ext_tab = "CREATE EXTERNAL TABLE "||ext_tname||
        " SAMEAS "||tname||" USING (" ||
        "DATAFILES('DISK:"||load_dir||"/"||tname||".unl'),"||
        "FORMAT 'DELIMITED', "|| "DELIMITER '|', "||
        "RECORDEND '', "||  "DELUXE, ESCAPE, "||
        "NUMROWS "|| numrows+1||", "||    "MAXERRORS  100, "||
        "REJECTFILE '"||load_dir||"/"||tname||".reject' " ||
        " )";

     LET ins = "INSERT INTO "||tname||"_ext SELECT * FROM "||tname;

     EXECUTE IMMEDIATE create_ext_tab;
     EXECUTE IMMEDIATE ins;

     LET rc = rc + 1;

END FOREACH

LET dbschema = "dbschema -q -d "||TRIM(dbname)||" -it DR -ss "||
                TRIM(load_dir)||"/"||TRIM(dbname)||".sql";

SYSTEM dbschema;
-- external tables are dropped after dbschema
FOREACH SELECT tabname
	INTO ext_tname 
	FROM systables
	WHERE tabtype = 'E'
	AND tabname MATCHES "*_ext"
	AND created = today

	LET drop_ext_tab = "DROP TABLE " || ext_tname;
	EXECUTE IMMEDIATE drop_ext_tab ;
END FOREACH

RETURN rc, bad;

END FUNCTION;

CREATE FUNCTION "informix".jm3_import(load_path VARCHAR(50),logmode char(3)) 
       RETURNING INTEGER, INTEGER 
DEFINE rc                INTEGER;
DEFINE bad               INTEGER;
DEFINE load_dir 	 LVARCHAR(512);
DEFINE create_tables_file LVARCHAR(512);
DEFINE dbname 		 nvarchar(250);
DEFINE create_ext_tab    LVARCHAR(8192);
DEFINE ins               LVARCHAR(512);
DEFINE drop_ext_tab      LVARCHAR(512);
DEFINE tname             VARCHAR(250);
DEFINE ext_tname         VARCHAR(250);
DEFINE dbschema          LVARCHAR(1024);
DEFINE create_tables_cmd         LVARCHAR(512);
DEFINE numrows           INTEGER;

LET rc=0;
LET bad=0;

--SET DEBUG FILE TO "/tmp/jm3_import.log";
--TRACE ON;
LET dbname = DBINFO("dbname")  ;
LET load_dir = TRIM(load_path) || "/" || TRIM(dbname) || ".exp" ;
LET create_tables_file = TRIM(load_dir) || "/" || TRIM(dbname) || ".sql" ;

LET create_tables_cmd = "dbaccess " || TRIM(dbname) || " " || create_tables_file ;
SYSTEM create_tables_cmd ;

FOREACH SELECT   TRIM(tabname),regex_replace(tabname, '_ext$','') 
     INTO ext_tname,tname
     FROM systables
     WHERE tabid >99 
     AND tabtype = "E"
     AND tabname MATCHES "*_ext"

     --LET ext_tname = tname||"_ext" ;
     --LET drop_ext_tab = "DROP TABLE IF EXISTS "||ext_tname;
     --EXECUTE IMMEDIATE drop_ext_tab;

     --LET create_ext_tab = "CREATE EXTERNAL TABLE "||ext_tname||
        --" SAMEAS "||tname||" USING (" ||
        --"DATAFILES('DISK:"||load_dir||"/"||tname||".unl'),"||
        --"FORMAT 'DELIMITED', "|| "DELIMITER '|', "||
        --"RECORDEND '', "||  "DELUXE, ESCAPE, "||
        --"NUMROWS "|| numrows+1||", "||    "MAXERRORS  100, "||
        --"REJECTFILE '"||load_dir||"/"||tname||".reject' " ||
        --" )";

     LET ins = "INSERT INTO "||tname || " SELECT * FROM "||ext_tname;
     -- EXECUTE IMMEDIATE ins;

     LET rc = rc + 1;

END FOREACH

RETURN rc, bad;

END FUNCTION;

