CREATE PROCEDURE "informix".p_ck_1to1relation_rmsparms_company() REFERENCING OLD AS o NEW AS n FOR rmsparm;
	--- this procedure simulates the behaviour of a foreign key by ALWAYS checking whether the 'parent' key exists or not
	DEFINE rows_count INTEGER;
	--#SET DEBUG FILE TO "/tmp/p_ck_1TO1Relation_rmsparms_company.out";
	--#trace on;

	IF (INSERTING) THEN  -- check if cmpy_code exists in company: if not, we raise an exception
		LET rows_count = ( SELECT count(*) FROM company WHERE company.cmpy_code = n.cmpy_code ) ;
		IF rows_count <> 1 THEN
			RAISE EXCEPTION -691,0,"Missing key in 1 to 1 relationship on cmpy_code";
		END IF
	END IF

	IF (DELETING) THEN   -- check if cmpy_code DOES NOT EXIST in company: if not, we raise an exception
		LET rows_count = ( SELECT count(*) FROM company WHERE company.cmpy_code = n.cmpy_code ) ;
		IF rows_count > 0 THEN
			RAISE EXCEPTION -692,0,"Key value for 1 to 1 relationship name is still being referenced";
		END IF
	END IF
END PROCEDURE;

create procedure "informix".p_ck_casetypes(l_case_type char(4) )
define nbrows int;
let nbrows = ( select count(*) from sr_types where sr_type = l_case_type);
if nbrows != 1 then
	raise exception -323;
end if
end procedure;

create procedure "informix".print_column_doc ( p_tabname varchar(128),p_colname varchar(128),p_language_code char(3)) returning nvarchar(255) ;
DEFINE p_documentation nvarchar(255);

IF p_language_code IS NULL THEN
   LET p_language_code = "ENU";
END IF;

LET p_documentation = 
( SELECT documentation FROM column_documentation
WHERE tabname = p_tabname
AND colname = p_colname
AND language_code = p_language_code ) ;

IF p_documentation IS NOT NULL THEN
	RETURN p_documentation;
ELSE
	RETURN "column not documented for this language";
END IF

end procedure;

create procedure "informix".print_table_doc ( p_tabname varchar(128),p_language_code char(3)) returning nvarchar(255) ;
DEFINE p_documentation nvarchar(255);

IF p_language_code IS NULL THEN
   LET p_language_code = "ENU";
END IF;

LET p_documentation = 
( SELECT documentation FROM table_documentation
WHERE tabname = p_tabname
AND language_code = p_language_code ) ;

IF p_documentation IS NOT NULL THEN
	RETURN p_documentation;
ELSE
	RETURN "table not documented for this language";
END IF

end procedure;

create procedure "informix".pull_table_contents_from (tabname varchar(128),source_dbname varchar(128),truncate_target char(10),special_statement LVARCHAR(1024) DEFAULT NULL);
DEFINE sql_statement LVARCHAR(2048);
SET DEBUG FILE TO "/tmp/pull_table_contents_from.dbg";
trace on;

IF ( truncate_target matches "trunc*" ) THEN
    LET sql_statement = "DELETE FROM " || trim(tabname) ;
    EXECUTE IMMEDIATE sql_statement;
END IF;
LET sql_statement = " INSERT INTO " || trim (tabname)  || " SELECT * FROM " || trim(source_dbname)|| ":" || trim(tabname) ;
EXECUTE IMMEDIATE sql_statement;

END PROCEDURE;

